/**
 * @RESTModel SignedAuthInfo
 *
 * @Required  [body,url]
 * @Property  {SignedAuthBody}    body            The POST request body
 * @Property  {string}            url             The URL to authenticate to
 */

/**
 * @RESTModel SignedAuthBody
 *
 * @Required  [expires,signature,userId]
 * @Property  {number}            expires         The timestamp (millis since epoch) at which the signed request information expires
 * @Property  {string}            signature       The signature for the signed request
 * @Property  {number}            userId          The id of the user to authenticate as
 */

/**
 * @RESTModel OauthClientList
 *
 * @Required  [count,rows]
 * @Property  {number}          count   The total number of oauth clients in the list
 * @Property  {OauthClient[]}   rows    The oauth clients in the list
 */

/**
 * @RESTModel OauthClient
 *
 * @Required [AppId,createdAt,displayName,id,redirectUri,updatedAt]
 * @Property {number}   AppId           The id of the app to which the client belongs
 * @Property {string}   createdAt       The string timestamp in which the oauth client was created
 * @Property {string}   displayName     The display name of the oauth client
 * @Property {number}   id              The id of the oauth client
 * @Property {string}   redirectUri     The redirect uri of the oauth client
 * @Property {string}   updatedAt       The string timestamp in which the oauth client was last updated
 * @Property {string}   disabled        The string timestamp in which the oauth client was disabled, if applicable
 * @Property {string}   secret          The secret of the oauth client
 */

/**
 * @RESTModel OauthAccessTokenList
 *
 * @Required  [count,rows]
 * @Property  {number}              count   The total number of oauth access tokens in the list
 * @Property  {OauthAccessToken[]}  rows    The oauth access tokens in the list
 */

/**
 * @RESTModel OauthAccessToken
 *
 * @Required [id,OauthClientId,token,UserId]
 * @Property {number}       id              The id of the access token
 * @Property {number}       OauthClientId   The id of the oauth client to which the token belongs
 * @Property {String}       token           The token string to use as the credential for making bearer requests
 * @Property {Number}       UserId          The id of the user that authorized the token
 * @Property {OauthClient}  OauthClient     The oauth client entity that owns the token
 * @Property {User}         User            The user entity that authorized the token
 */

/**
 * @RESTModel OauthAccessTokenExchange
 *
 * @Required [token]
 * @Property {string}   access_token    The string access token to use in `Authorization: Bearer <access_token>` OAuth requests
 */
