var GrassHopper = require('gh-core');

var ConfigAPI = require('./api');

/**
 * @REST getConfig
 *
 * Get the configuration for the current app
 *
 * @Server      app
 * @Method      GET
 * @Path        /config
 * @Return      {Config}                            The configuration for the current app
 */
/**
 * @REST getConfigByApp
 *
 * Get the configuration for an app
 *
 * @Server      admin
 * @Method      GET
 * @Path        /config
 * @QueryParam  {number}            app             The id of the app to get the configuration for
 * @Return      {Config}                            The configuration for the specified app
 */
var _getConfig = function(req, res) {
    var appId = req.query.app || req.ctx.app.id;
    ConfigAPI.getApplicationConfig(req.ctx, appId, function(err, config) {
        if (err) {
            return res.status(err.code).send(err.msg);
        }

        return res.status(200).send(config);
    });
};

GrassHopper.appRouter.on('get', '/api/config', _getConfig);
GrassHopper.globalAdminRouter.on('get', '/api/config', _getConfig);

/**
 * @REST updateConfig
 *
 * Update a configuration value for the current app
 *
 * @Server      app
 * @Method      POST
 * @Path        /config
 * @BodyParam   {ConfigValues}      body            Configuration value(s) to update
 * @Return      {void}
 */
/**
 * @REST updateConfigByApp
 *
 * Update a configuration value for an app
 *
 * @Server      admin
 * @Method      POST
 * @Path        /config
 * @FormParam   {number}            app             The id of the app to update the configuration value(s) for
 * @BodyParam   {ConfigValues}      body            Configuration value(s) to update
 * @Return      {void}
 */
var _updateConfig = function(req, res) {
    var appId = req.body.app || req.ctx.app.id;
    delete req.body.app;
    ConfigAPI.updateConfig(req.ctx, appId, req.body, function(err) {
        if (err) {
            return res.status(err.code).send(err.msg);
        }

        return res.sendStatus(200);
    });
};

GrassHopper.appRouter.on('post', '/api/config', _updateConfig);
GrassHopper.globalAdminRouter.on('post', '/api/config', _updateConfig);
