        assert.strictEqual(validator.getErrorCount(), 1);

        // Multiple success
        validator = new Validator();
        validator.check('nicolaas.matthijs@caret.cam.ac.uk').isEmail();
        validator.check('bertpareyn@gmail.com').isEmail();
