/**
 * Assert that the organisational units, event series and events under an organisational unit can be exported in CSV
 *
 * @param  {RestClient}         client                  The REST client to make the request with
 * @param  {Number}             id                      The id of the organisational unit to export
 * @param  {Function}           callback                Standard callback function
 * @param  {Object[]}           callback.records        The exported organisational units, event series and events
 * @throws {AssertionError}                             Error thrown when an assertion failed
 * @api private
 */
var _assertExportOrgUnitCSV = function(client, id, callback) {
    // Get all the data under the organisational unit to verify the correct data is present
    _getExportData(client, id, function(orgUnit, childOrgUnits, allSeries, eventsPerSeriesId) {
        var allEvents = _.flatten(_.values(eventsPerSeriesId));
        var allOrgUnits = _.union([orgUnit], childOrgUnits);

        // Get the CSV data
        client.orgUnit.exportOrgUnit(id, 'csv', function(err, data, response) {
            assert.ok(!err);

            assert.strictEqual(response.headers['content-type'], 'text/csv; charset=utf-8');

            // Assert the CSV data can be parsed
            csv.parse(data, {'columns': true}, function(err, records) {
                assert.ok(!err);

                // Get the different types of organisational units
                var types = _.uniq(_.pluck(allOrgUnits, 'type'));

                // Get the column headers for the organisational unit ids
                var orgUnitIdColumnNames = _.chain(records[0])
                    .keys()
                    .filter(function(column) {
                        return (column.indexOf('_id') > 0 && column !== 'series_id' && column !== 'event_id' && column !== 'organiser_id');
                    })
                    .value();

                // There should be at least as many organisational unit id columns as there are
                // different types in the tree. Because a type can appear on multiple levels, there
                // could be more orgUnitIdColumnNames than types
                assert.ok(orgUnitIdColumnNames.length >= types.length);

                // Assert the correct data is returned
                _.each(records, function(record) {
                    if (record.series_id) {
                        var series = _.find(allSeries, {'id': _.parseInt(record.series_id)});
                        assert.ok(series);
                        assert.strictEqual(record.series_externalId, series.externalId || '');
                        assert.strictEqual(record.series_displayName, series.displayName);
                        assert.strictEqual(record.series_description, series.description || '');
                    }
                    if (record.event_id) {
                        var event = _.find(allEvents, {'id': _.parseInt(record.event_id)});
                        assert.ok(event);
                        assert.strictEqual(record.event_externalId, event.event_externalId || '');
                        assert.strictEqual(record.event_displayName, event.displayName);
                        assert.strictEqual(record.event_description, event.description || '');
                        assert.strictEqual(record.event_notes, event.notes || '');
                        assert.strictEqual(record.event_location, event.location || '');
                        assert.strictEqual(new Date(record.event_start).getTime(), new Date(event.start).getTime());
                        assert.strictEqual(new Date(record.event_end).getTime(), new Date(event.end).getTime());
                        assert.strictEqual(record.event_type, event.type || '');
                    }

                    _.each(orgUnitIdColumnNames, function(idColumnName) {
                        if (record[idColumnName]) {
                            // Assert an organisational unit with this ID should've been exported
                            var orgUnit = _.find(allOrgUnits, {'id': _.parseInt(record[idColumnName])});
                            assert.ok(orgUnit);

                            // Assert the metadata of the columns
                            var columnPrefix = idColumnName.slice(0, -3);
                            assert.strictEqual(record[columnPrefix + '_description'], orgUnit.description || '');
                            assert.strictEqual(record[columnPrefix + '_displayName'], orgUnit.displayName);
                            assert.strictEqual(record[columnPrefix + '_externalId'], orgUnit.externalId || '');
                            assert.strictEqual(record[columnPrefix + '_published'], orgUnit.published ? '1' : '');
                            assert.strictEqual(record[columnPrefix + '_parentId'], orgUnit.ParentId ? ('' + orgUnit.ParentId) : '');
                        }
                    });
                });

                return callback(records);
            });
        });
    });
};

/**
 * Assert that the organisational units, event series and events under an organisational unit can be exported in JSON
 *
 * @param  {RestClient}         client                  The REST client to make the request with
 * @param  {Number}             id                      The id of the organisational unit to export
 * @param  {Function}           callback                Standard callback function
 * @param  {Object}             callback.data           The exported organisational units, event series and events
 * @throws {AssertionError}                             Error thrown when an assertion failed
 * @api private
 */
var _assertExportOrgUnitJSON = function(client, id, callback) {
    // Get all the data under the organisational unit to verify the correct data is present
    _getExportData(client, id, function(orgUnit, childOrgUnits, allSeries, eventsPerSeriesId) {
        var allEvents = _.flatten(_.values(eventsPerSeriesId));
        var allOrgUnits = _.union([orgUnit], childOrgUnits);

        // Export the organisational unit
        client.orgUnit.exportOrgUnit(id, 'json', function(err, data, response) {
            assert.ok(!err);

            assert.strictEqual(response.headers['content-type'], 'application/json; charset=utf-8');
            assert.ok(_.isObject(data));

            var _assertExportOrgUnitJSONOrgUnit = function(exportedOrgUnit) {
                var expectedOrgUnit = _.find(allOrgUnits, {'id': exportedOrgUnit.id});
                assert.ok(expectedOrgUnit);
                assert.strictEqual(exportedOrgUnit.externalId, expectedOrgUnit.externalId);
                assert.strictEqual(exportedOrgUnit.displayName, expectedOrgUnit.displayName);
                assert.strictEqual(exportedOrgUnit.description, expectedOrgUnit.description);
                assert.strictEqual(exportedOrgUnit.published, expectedOrgUnit.published);
                assert.strictEqual(exportedOrgUnit.type, expectedOrgUnit.type);
                assert.strictEqual(exportedOrgUnit.ParentId, expectedOrgUnit.ParentId);
                assert.strictEqual(exportedOrgUnit._type, 'orgUnit');

                // Assert the correct series are present
                var exportedSeries = exportedOrgUnit.series;
                var expectedSeriesForOrgUnit = expectedOrgUnit.Series || [];

                assert.strictEqual(exportedSeries.length, expectedSeriesForOrgUnit.length);
                _.each(exportedSeries, function(series) {
                    var expectedSeries = _.find(expectedSeriesForOrgUnit, {'id': series.id});
                    assert.ok(expectedSeries);
                    assert.strictEqual(series.externalId, expectedSeries.externalId);
                    assert.strictEqual(series.displayName, expectedSeries.displayName);
                    assert.strictEqual(series.description, expectedSeries.description);
                    assert.strictEqual(series.type, expectedSeries.type);
                    assert.strictEqual(series._type, 'series');

                    // Assert the correct events are present
                    assert.strictEqual(series.events.length, eventsPerSeriesId[series.id].length);
                    _.each(series.events, function(exportedEvent) {
                        var expectedEvent = _.find(eventsPerSeriesId[series.id], {'id': exportedEvent.id});
                        assert.ok(expectedEvent);
                        assert.strictEqual(exportedEvent.externalId, expectedEvent.externalId);
                        assert.strictEqual(exportedEvent.displayName, expectedEvent.displayName);
                        assert.strictEqual(exportedEvent.description, expectedEvent.description);
                        assert.strictEqual(exportedEvent.start, expectedEvent.start);
                        assert.strictEqual(exportedEvent.end, expectedEvent.end);
                        assert.strictEqual(exportedEvent.location, expectedEvent.location);
                        assert.strictEqual(exportedEvent.type, expectedEvent.type);
                        assert.strictEqual(exportedEvent._type, 'event');

                        // Assert the correct organisers are present
                        assert.strictEqual(exportedEvent.organisers.length, expectedEvent.organisers.length);
                        _.each(exportedEvent.organisers, function(organiser) {
                            if (organiser.id) {
                                var expectedOrganiser = _.find(expectedEvent.organisers, {'id': organiser.id});
                                assert.ok(expectedOrganiser);
                                assert.strictEqual(organiser.displayName, expectedOrganiser.displayName);
                                assert.strictEqual(organiser.shibbolethId, expectedOrganiser.shibbolethId);
                            } else {
                                assert.ok(_.contains(expectedEvent.organisers, organiser.displayName));
                            }
                        });
                    });
                });
                // Assert the correct children were returned for this organisational unit
                var expectedChildren = _.filter(allOrgUnits, {'ParentId': exportedOrgUnit.id});
                assert.strictEqual(exportedOrgUnit.children.length, expectedChildren.length);
                _.each(exportedOrgUnit.children, function(exportedChild) {
                    var expectedChild = _.find(expectedChildren, {'id': exportedChild.id});
                    assert.ok(expectedChild);

                    // Drill down into each child and assert it contains the correct properties, series and/or events
                    _assertExportOrgUnitJSONOrgUnit(exportedChild);
                });
            };

            // Start asserting the data
            _assertExportOrgUnitJSONOrgUnit(data);

            return callback(data);
        });
    });
};

/**
 * Get all the data that should be included in an exported organisational unit
 *
 * @param  {RestClient}         client                  The REST client to make the request with
 * @param  {Number}             id                      The id of the organisational unit to export
 * @param  {Function}           callback                Standard callback function
 * @param  {OrgUnit}            callback.orgUnit        The organisational unit that should be exported
 * @param  {OrgUnit[]}          callback.children       The organisational units under the exported organisational unit
 * @param  {Serie[]}            callback.series         The series under the exported organisational unit, including indirect series
 * @param  {Events[]}           callback.event          The events under the exported organisational unit, including indirect events
 * @throws {AssertionError}                             Error thrown when an assertion failed
 * @api private
 */
var _getExportData = function(client, id, callback) {
    // Get the organisational unit so we can verify its metadata in the JSON tree
    assertGetOrgUnit(client, id, false, function(orgUnit) {

        // Get the tree under the organisational unit so we can verify the correct data was returned
        _getAllChildren(client, orgUnit.AppId, [id], function(children, series) {

            // Get the events for all the series
            var allSeriesIds = _.pluck(series, 'id');
            SeriesTestsUtil.getAllSeriesEvents(client, allSeriesIds, function(eventsPerSeriesId) {

                return callback(orgUnit, children, series, eventsPerSeriesId);
            });
        });
    });
};

/**
 * Get all the child organisational units under a given set of parent organisational units
 *
 * @param  {RestClient}         client                  The REST client to make the request with
 * @param  {Number}             appId                   The id of the application in which the parent organisational units are located
 * @param  {Number[]}           parents                 The ids of the parent organisational units for which to retrieve the children
 * @param  {Function}           callback                Standard callback function
 * @param  {OrgUnit[]}          callback.children       The organisational units under the exported organisational unit
 * @param  {Serie[]}            callback.series         The series under the exported organisational unit, including indirect series
 * @throws {AssertionError}                             Error thrown when an assertion failed
 * @api private
 */
var _getAllChildren = function(client, appId, parents, callback, _children, _series) {
    _children = _children || [];
    _series = _series || [];

    if (_.isEmpty(parents)) {
        return callback(_children, _series);
    }
    var parent = parents.shift();
    assertGetOrgUnits(client, appId, true, false, parent, null, function(data) {
        // Add the retrieved organisational units
        _children = _.union(_children, data.results);

        // Add the ids of the children to the set of parent ids so we can get the grand children
        parents = _.union(parents, _.pluck(data.results, 'id'));

        // Keep track of all the series
        _.each(data.results, function(child) {
            _series = _.union(_series, child.Series);
        });

        // Get the children for the next organisational unit
        _getAllChildren(client, appId, parents, callback, _children, _series);
    });
};

