// Always run in UTC, no matter what
process.env.TZ = 'UTC';

var log = require('gh-core/lib/logger').logger('before-tests');

var TestsUtil = require('gh-tests/lib/util');

// Bootstrap the application server before the tests begin
before(function(callback) {
    // Create the configuration for the test
    var config = TestsUtil.createInitialTestConfig();

    // Start up the application server
    TestsUtil.setUpBeforeTests(config, callback);
});

// To help out with debugging test failures, we log which test is about to begin
beforeEach(function(callback) {
    log().info('Beginning test "%s"', this.currentTest.title);
    return callback();
});

// To help out with debugging test failures, we log which test has just finished
afterEach(function(callback) {
    log().info('Finishing test "%s"', this.currentTest.title);
    return callback();
});
