/**
 * Determine whether or not the user in context can update a user
 *
 * @param  {Context}    ctx                         Standard context object containing the current user and the current application
 * @param  {User}       user                        The user to update
 * @param  {Function}   callback                    Standard callback function
 * @param  {Object}     callback.err                An error that occurred, if any
 * @param  {Boolean}    callback.canUpdate          `true` if the user in context has the appropriate permission. `false` otherwise
 */
var canUpdateUser = module.exports.canUpdateUser = function(ctx, user, callback) {
    // Anonymous users cannot update a user
    if (!ctx.user) {
        return callback(null, false);

    // Application and global admins can update a user
    } else if (ctx.user.canAdmin(user.AppId)) {
        return callback(null, true);

    // A user can update his own account
    } else if (ctx.user.id === user.id) {
        return callback(null, true);
    }

    // Everyone else cannot update user
    return callback(null, false);
};

/**
 * Determine whether or not the user in context can update the app administrator status of a user
 *
 * @param  {Context}    ctx                         Standard context object containing the current user and the current application
 * @param  {User}       user                        The user to update the app administrator status for
 * @param  {Function}   callback                    Standard callback function
 * @param  {Object}     callback.err                An error that occurred, if any
 * @param  {Boolean}    callback.canUpdate          `true` if the user in context has the appropriate permission. `false` otherwise
 */
var canUpdateAdminStatus = module.exports.canUpdateAdminStatus = function(ctx, user, callback) {
    // Anonymous users cannot update the admin status of a user
    if (!ctx.user) {
        return callback(null, false);
    }

    // Only application and global admins can update the admin status of a user
    var canUpdate = ctx.user.canAdmin(user.AppId);
    return callback(null, canUpdate);
};

/**
 * Determine whether or not the user in context can reset a user's calendar token
 *
 * @param  {Context}    ctx                         Standard context object containing the current user and the current application
 * @param  {User}       user                        The user to reset the calendar token for
 * @param  {Function}   callback                    Standard callback function
 * @param  {Object}     callback.err                An error that occurred, if any
 * @param  {Boolean}    callback.canResetToken      `true` if the user in context has the appropriate permission. `false` otherwise
 */
var canResetUserToken = module.exports.canResetUserToken = function(ctx, user, callback) {
    // Resetting a user's calendar token requires the same permissions as updating the user
    return canUpdateUser(ctx, user, callback);
};

/**
 * Determine whether or not the user in context can accept the Terms and Conditions for a user
 *
 * @param  {Context}    ctx                                         Standard context object containing the current user and the current application
 * @param  {User}       user                                        The user to accept the Terms and Conditions for
 * @param  {Function}   callback                                    Standard callback function
 * @param  {Object}     callback.err                                An error that occurred, if any
 * @param  {Boolean}    callback.canAcceptTermsAndConditions        `true` if the user in context has the appropriate permission. `false` otherwise
 */
var canAcceptTermsAndConditions = module.exports.canAcceptTermsAndConditions = function(ctx, user, callback) {
    if (!ctx.user) {
        return callback(null, false);
    }

    var canAccept = (ctx.user.id === user.id);
    return callback(null, canAccept);
};

/**
 * Determine whether or not the user in context can get a user's calendar
 *
 * @param  {Context}    ctx                         Standard context object containing the current user and the current application
 * @param  {User}       user                        The user to get the calendar for
 * @param  {String}     [token]                     An optional access control token
 * @param  {Function}   callback                    Standard callback function
 * @param  {Object}     callback.err                An error that occurred, if any
 * @param  {Boolean}    callback.canGetCalendar     `true` if the user in context has the appropriate permission. `false` otherwise
 */
var canGetUserCalendar = module.exports.canGetUserCalendar = function(ctx, user, token, callback) {
    // If a token was provided and matches the one that
    // is stored for the user, the calendar can be retrieved
    if (token && user.calendarToken === token) {
        return callback(null, true);

    // Anonymous users cannot retrieve a calendar
    } else if (!ctx.user) {
        return callback(null, false);

    // Application and global admins can retrieve a user's calendar
    } else if (ctx.user.canAdmin(user.AppId)) {
        return callback(null, true);

    // A user can retrieve their own calendar
    } else if (ctx.user.id === user.id) {
        return callback(null, true);
    }

    // Everyone else cannot retrieve the calendar
    return callback(null, false);
};

/**
 * Determine whether or not the user in context can import users
 *
 * @param  {Context}    ctx                         Standard context object containing the current user and the current application
 * @param  {App}        app                         The application to import the users into
 * @param  {Function}   callback                    Standard callback function
 * @param  {Object}     callback.err                An error that occurred, if any
 * @param  {Boolean}    callback.canImportUsers     `true` if the user in context has the appropriate permission. `false` otherwise
 */
var canImportUsers = module.exports.canImportUsers = function(ctx, app, callback) {
    // Anonymous users can never import users
    if (!ctx.user) {
        return callback(null, false);
    }

    // Only global administrators can import users
    return callback(null, ctx.user.isGlobalAdmin());
};
